mapper.insert:
INSERT INTO t_overflow_list
(
overflow_number, overflow_date, remarks, user_id
)
VALUES
(
#{overflowNumber}, #{overflowDate}, #{remarks}, #{userId}
)

INSERT INTO t_overflow_list_goods
(
goods_id, goods_code, goods_name, goods_model, goods_unit,
goods_num, price, total, overflow_list_id, goods_type_id
)
VALUES
(
#{goodsId}, #{goodsCode}, #{goodsName}, #{goodsModel}, #{goodsUnit},
#{goodsNum}, #{price}, #{total}, #{overflowListId}, #{goodsTypeId}
)

mapper.select:
SELECT t_overflow_list.overflow_list_id, t_overflow_list.overflow_number, t_overflow_list.overflow_date, t_overflow_list.remarks, t_overflow_list.user_id, t_user.true_name FROM t_overflow_list LEFT JOIN t_user ON t_overflow_list.user_id = t_user.user_id
AND t_overflow_list.overflow_date >= #{sTime}
AND t_overflow_list.overflow_date <= #{eTime}
